const { Link } = require('react-router-dom');
const LinkOut = require('./LinkOut');
const React = require('react');

module.exports = ({friend, url, selected}) => (
  <div
    className={'component-friend-profile' + (selected ? ' selected' : '')}
    title={friend.name}
  >
    <Link className='plain' to={url}>
      <div><img src={friend.avatarUrl} /></div>
      <div className='info'>
        <strong>{friend.name}</strong>
        <div className='help'>{
          friend.realName ? `(${friend.realName})` : '\xa0'
        }</div>
      </div>
    </Link>
    <div><LinkOut url={friend.profileUrl}>Profile</LinkOut></div>
  </div>
);
