const React = require('react');
const sharedUtils = require('shared/utils');

module.exports = ({games}) => {
  games = games.slice().sort((a, b) => sharedUtils.strcmpCaseInsensitive(a.name, b.name));
  return (
    <div className='component-game-list'>
      <div>{'You and the selected user(s) have the following games in common:'}</div>
      <div className='list-wrapper'>
        <div className='list'>{
          games.map((game) => (
            <div className='game' key={game.id} title={game.name}>
              <div>
                <div className='logo' style={{backgroundImage: `url(${game.logoUrl})`}}></div>
              </div>
              <strong>{game.name}</strong>
            </div>
          ))
        }</div>
      </div>
    </div>
  );
};
