const React = require('react');

module.exports = ({children, className, hideIcon, plain, url}) => (
  <a
    className={`${className || ''} ${plain ? 'plain' : ''}`.trim() || null}
    href={url}
    rel='noopener'
    target='_blank'
  >
    {children}{ (hideIcon || plain) ? null : ' \uD83E\uDC65'}
  </a>
);
