const React = require('react');

const EXTERNAL_LINK_REGEX = /^https?:\/\//;

module.exports = ({children, className, hideIcon, plain, url, sameTab}) => {
  return (
    <a
      className={`${className || ''} ${plain ? 'plain' : ''}`.trim() || null}
      href={url}
      rel='noopener'
      target={sameTab ? null : '_blank'}
    >
      {children}{ (hideIcon || plain) ? null : ' \uD83E\uDC65'}
    </a>
  );
}
