const { Link } = require('react-router-dom');
const LinkOut = require('./LinkOut');
const React = require('react');

module.exports = ({user}) => (
  <div className='component-user-profile'>
    <div><img src={user.avatarUrl} /></div>
    <div className='info'>
      <h2>{user.name}</h2>
      {user.realName && <div className='help'>({user.realName})</div>}
      <div><LinkOut url={user.profileUrl}>Profile</LinkOut></div>
      <div><Link to='/'>Log out</Link></div>
    </div>
  </div>
);
