const React = require('react');
const GameListAjax = require('components/GameListAjax');
const Profiles = require('components/Profiles');

module.exports = class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userList: [],
    };
  }
  onUserListChange(userList) {
    this.setState({userList});
  }
  render() {
    const id = this.props.match.params.id;
    return (
      <div>
        <Profiles name={id} onListChange={(list) => this.onUserListChange(list)} />
        {
          this.state.userList.length ? (
            <section>
              <GameListAjax userIdString={this.state.userList.join(',')} />
            </section>
          ) : null
        }
      </div>
    );
  }
}
