const dotenv = require('dotenv');
const path = require('path');

const ROOT_DATA_DIR = path.resolve('data');
const APP_DETAILS_DIR = path.resolve(ROOT_DATA_DIR, 'app-details');

const config = {
  APP_DETAILS_DIR,
  ROOT_DATA_DIR,
};

// Required environment variables that will be exported from this module
const ENVIRONMENT_VARIABLES = [
  'PORT',
  'STEAM_API_KEY',
];

// In dev mode, read from .env file
if (process.env.NODE_ENV !== 'production') {
  dotenv.config();
}

const getEnvironmentVariable = (name) => {
  const value = process.env[name];
  if (!value) {
    throw new Error(`Missing required environment variable ${name}`);
  }
  return value;
}

for (const name of ENVIRONMENT_VARIABLES) {
  config[name] = getEnvironmentVariable(name);
}

module.exports = config;
