
const strcmp = (a, b) => (a < b) ? -1 : ((a > b) ? 1 : 0);

const strcmpCaseInsensitive = (a, b) => strcmp(a.toLowerCase(), b.toLowerCase());

const waitSeconds = (seconds) => new Promise((resolve, reject) => {
  setTimeout(() => resolve(), seconds * 1000);
});

module.exports = {
  strcmp,
  strcmpCaseInsensitive,
  waitSeconds,
};
